// Extract and process incoming call data
const callData = items[0].json;

// Clinic information
const clinicInfo = {
  name: 'Deane Eye Clinic',
  address: '222 Deane Rd, Deane, Bolton BL3 5DP',
  phone: '01204 524785',
  hours: {
    open: '09:30',
    close: '17:30',
    days: ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday']
  },
  services: [
    { name: 'Eye Examination', duration: 30, price: 45 },
    { name: 'Contact Lens Fitting', duration: 45, price: 65 },
    { name: 'Emergency Consultation', duration: 15, price: 35 },
    { name: 'Children Eye Test', duration: 25, price: 40 },
    { name: 'Diabetic Eye Screening', duration: 20, price: 50 }
  ]
};

// Determine call intent and prepare response
const processedData = {
  caller_number: callData.From || callData.caller_id,
  call_time: new Date().toISOString(),
  clinic_info: clinicInfo,
  session_id: `deane_${Date.now()}`,
  call_type: 'incoming'
};

return [{ json: processedData }];

// VAPI Assistant Configuration for Deane Eye Clinic
const assistantConfig = {
  model: {
    provider: 'openai',
    model: 'gpt-4',
    temperature: 0.7,
    maxTokens: 500
  },
  voice: {
    provider: 'elevenlabs',
    voiceId: 'pNInz6obpgDQGcFmaJgB', // British female voice
    stability: 0.5,
    similarityBoost: 0.8
  },
  name: 'Sarah - Deane Eye Clinic Receptionist',
  firstMessage: "Hello, thank you for calling Deane Eye Clinic. I'm Sarah, your AI assistant. How may I help you today?",
  systemPrompt: `You are Sarah, a professional and friendly AI receptionist for Deane Eye Clinic.

CLINIC INFORMATION:
- Name: Deane Eye Clinic
- Address: 222 Deane Rd, Deane, Bolton BL3 5DP
- Phone: 01204 524785
- Hours: 9:30 AM - 5:30 PM, seven days a week

SERVICES OFFERED:
- Comprehensive eye examinations (30 min, ¬£45)
- Contact lens fittings and aftercare (45 min, ¬£65)
- Emergency eye consultations (15 min, ¬£35)
- Children's eye tests (25 min, ¬£40)
- Diabetic eye screening (20 min, ¬£50)
- Spectacle dispensing and adjustments

YOUR ROLE:
1. Greet callers warmly and professionally
2. Help with appointment bookings
3. Provide clinic information (hours, location, services)
4. Handle appointment changes and cancellations
5. For emergencies, prioritize urgent care
6. Collect patient details: name, phone, email, preferred date/time
7. Always confirm appointment details before ending the call

GUIDELINES:
- Be empathetic and patient-focused
- Speak clearly and at a moderate pace
- Ask clarifying questions when needed
- For complex medical questions, offer to connect with a clinician
- Always end calls professionally with next steps

EMERGENCY PROTOCOL:
For urgent eye problems (sudden vision loss, severe pain, injury), immediately offer to:
1. Schedule same-day emergency appointment
2. Provide after-hours emergency contact
3. Advise to visit A&E if extremely urgent`,
  functions: [
    {
      name: 'check_availability',
      description: 'Check appointment availability for specific dates and times',
      parameters: {
