      }
    }
  ],
  endCallMessage: "Thank you for calling Deane Eye Clinic. Have a wonderful day and we look forward to seeing you soon!",
  endCallPhrases: ["goodbye", "thank you", "that's all", "bye"],
  recordingEnabled: true,
  maxDurationSeconds: 600
};

return [{
  json: {
    ...items[0].json,
    vapi_assistant_config: assistantConfig
  }
}];

// Process calendar data and find available slots
const calendarData = items[0].json;
const requestedDate = $json.date;
const serviceType = $json.service;

// Clinic operating hours
const clinicHours = {
  start: '09:30',
  end: '17:30'
};

// Service durations (in minutes)
const serviceDurations = {
  'Eye Examination': 30,
  'Contact Lens Fitting': 45,
  'Emergency Consultation': 15,
  'Children Eye Test': 25,
  'Diabetic Eye Screening': 20
};

// Generate time slots
function generateTimeSlots(startTime, endTime, duration) {
  const slots = [];
  const start = new Date(`2024-01-01T${startTime}:00`);
  const end = new Date(`2024-01-01T${endTime}:00`);

  while (start < end) {
    slots.push(start.toTimeString().slice(0, 5));
    start.setMinutes(start.getMinutes() + duration);
  }

  return slots;
}

// Check for conflicts with existing appointments
function isSlotAvailable(timeSlot, existingEvents, duration) {
  const slotStart = new Date(`${requestedDate}T${timeSlot}:00`);
  const slotEnd = new Date(slotStart.getTime() + duration * 60000);

  for (const event of existingEvents) {
    const eventStart = new Date(event.start.dateTime || event.start.date);
    const eventEnd = new Date(event.end.dateTime || event.end.date);

    if (slotStart < eventEnd && slotEnd > eventStart) {
      return false;
    }
  }

  return true;
}

const duration = serviceDurations[serviceType] || 30;
const allSlots = generateTimeSlots(clinicHours.start, clinicHours.end, duration);
const existingEvents = calendarData.items || [];

const availableSlots = allSlots.filter(slot => 
  isSlotAvailable(slot, existingEvents, duration)
);

return [{
  json: {
    date: requestedDate,
    service: serviceType,
    duration: duration,
    available_slots: availableSlots,
    total_slots: allSlots.length,
    booked_slots: allSlots.length - availableSlots.length
  }
}];

// Calculate appointment end time and prepare booking data
const appointmentData = items[0].json;

// Service durations
const serviceDurations = {
  'Eye Examination': 30,
  'Contact Lens Fitting': 45,
  'Emergency Consultation': 15,
  'Children Eye Test': 25,
  'Diabetic Eye Screening': 20
};

const duration = serviceDurations[appointmentData.service] || 30;
const startTime = new Date(`${appointmentData.date}T${appointmentData.time}:00`);
const endTime = new Date(startTime.getTime() + duration * 60000);

return [{
  json: {
    ...appointmentData,
    duration: duration,
    end_time: {
      dateTime: endTime.toISOString(),
      timeZone: 'Europe/London'
    },
    booking_reference: `DEANE${Date.now().toString().slice(-6)}`
  }
}];

